export default {
  data () {
    return {
      deliveryViaGAM: "",
      PRchecked: false,
      isExtendIn: "拡大しない",
      closeButtonAdd: "不要",
      isCloseButtonImageChange: false,
      normalGAMheadTag: "",
      normalGAMbodyTag: "",

      zoneType: "",
      zoneId: "",
      aladdinTag: "",
      height: "",
      prepend: "",
      append: "",
      rotateStatus: {
        period: 10000,
        count: 0,
        isWheelIn: false,
        isRemoveBottomSpace: true,
        isUpshift: true,
        iconSize: 30
      },
      prBarStatus: {
        prBackground: "#000000",
        prTextHeight: 20,
        prTextColor: "#FFFFFF",
        prTextSize: 15,
        prText: "[PR]"
      },
      closeButtonStatus: {
        closeButtonPos: "左",
        closeButtonSize: 16,
        closeButtonUrl: "https://works.gsspcln.jp/w/ad_format/close_icons/close_icon_bold.png"
      },

      creativeTag: "",
      GAMheadTag: "",
      GAMbodyTag: ""
    }
  },

  methods: {
    inputPrepend: function () {
      if (this.zoneType === "スタンダードバナー"){
        this.prepend = "<div id="geniee_overlay" style="height: " + this.height + "px; width: 100%;">";
      } else {
        this.prepend = "";
      }
    },

    inputAppend: function () {
      if (this.zoneType === "スタンダードバナー"){
        this.append = "";
        if (this.PRchecked){
          this.append += "<div style="background-color:" + this.prBarStatus.prBackground + ";text-align:center; width:320px; height:" + this.prBarStatus.prTextHeight + "px; color:" + this.prBarStatus.prTextColor + "; margin: 0 auto; font-size:" + this.prBarStatus.prTextSize + "px;">" + this.prBarStatus.prText + "</div>
"
        }
        this.append += "</div>
<style> .gn_ov-fadeout-content{ animation-name: fadeoutOV; animation-duration: 0.5s; animation-timing-function: ease-out; transform-origin: 0 50%; } @keyframes fadeoutOV { 0% { -webkit-transform:rotateX(90deg); -moz-transform:rotateX(90deg); transform:rotateX(90deg); opacity: 0; } 50% { -webkit-transform:rotateX(45deg); -moz-transform:rotateX(45deg); transform:rotateX(45deg); opacity: 0.5; } 100% { -webkit-transform:rotateX(0deg); -moz-transform:rotateX(0deg); transform:rotateX(0deg); opacity: 1; } } </style>
<script>
  (function(window, document){
    if (parent.document.getElementsByClassName("gnROV")[0] && parent.document.getElementsByClassName("gnROV")[0].dataset["isWheelIn"] === "true") document.getElementById("geniee_overlay").classList.add("gn_ov-fadeout-content");
  })(window, document);
</scr" + "ipt>
";


      } else if (this.zoneType === "SPオーバーレイ") {
        this.append = "";
        this.append += "<style> .gn_ov-fadeout-content{ animation-name: fadeoutOV; animation-duration: 0.5s; animation-timing-function: ease-out; transform-origin: 0 50%; } @keyframes fadeoutOV { 0% { -webkit-transform:rotateX(90deg); -moz-transform:rotateX(90deg); transform:rotateX(90deg); opacity: 0; } 50% { -webkit-transform:rotateX(45deg); -moz-transform:rotateX(45deg); transform:rotateX(45deg); opacity: 0.5; } 100% { -webkit-transform:rotateX(0deg); -moz-transform:rotateX(0deg); transform:rotateX(0deg); opacity: 1; } } </style>
<script>
  (function(window, document){
    if (parent.document.getElementsByClassName("gnROV")[0] && parent.document.getElementsByClassName("gnROV")[0].dataset["isWheelIn"] === "true") document.getElementById("geniee_overlay").classList.add("gn_ov-fadeout-content");
  })(window, document);
</scr" + "ipt>
";
        if (this.PRchecked){
          this.append += "<script type="text/javascript">
(function(window, document) {
  var adStr = "" + this.prBarStatus.prText + "",
  bottomHeight = "" + this.prBarStatus.prTextHeight + "px",
  ovBase = document.getElementById("geniee_overlay");
  ovBase.style.bottom = bottomHeight;
  createAdInfo();

  function createAdInfo() {
    var div = document.createElement("div");
    div.setAttribute("style", "background-color:" + this.prBarStatus.prBackground + ";text-align:center;width:320px;color:" + this.prBarStatus.prTextColor + "!important;margin: 0 auto; font-size:" + this.prBarStatus.prTextSize + "px;");
    div.setAttribute("id", "gn_expand_area");
    div.style.height = bottomHeight;
    div.innerHTML = adStr;
    ovBase.appendChild(div);
  }
})(window, document);
</scr" + "ipt>
"
        }
      }
      if (this.isExtendIn === "Aladdin配信時のみ" && this.deliveryViaGAM === "Aladdinタグ直貼り"){
        var extendCode = "<script>
    (function (window, document) {
        var gnROV = parent.document.getElementsByClassName("gnROV")[0];
        var s = window.top.innerWidth / 320;

        gnROV.style.transform = "scale(" + s + ")";
        gnROV.style.transformOrigin = "bottom";
        window.frameElement.style.height =  "" + this.height + "px";
    })(window, document);
</scr" + "ipt>
";
        this.append += extendCode;
      }
      if (this.closeButtonAdd ===  "Aladdin配信時のみ" && this.deliveryViaGAM === "Aladdinタグ直貼り"){
        var closeButtonPosition = "";
        var closeButtonSetting = "";
        closeButtonPosition = "closeIcon.style.left = "0px";
";
        if (this.closeButtonStatus.closeButtonPos === "左") closeButtonPosition = "closeIcon.style.left = "0px";
";
        else if (this.closeButtonStatus.closeButtonPos === "中央") closeButtonPosition = "";
        else if (this.closeButtonStatus.closeButtonPos === "右") closeButtonPosition = "closeIcon.style.right = "0px";
";
        closeButtonSetting = "<script>
(function(window, document){
    if (parent.document.getElementById("geniee_overlay_close")) return;
    var geniee_overlay = parent.document.getElementsByClassName("gnROV")[0];
    var gn_delivery = parent.document.getElementsByClassName("gnROVin")[0];
    var closeIcon = document.createElement("img");
    var buttonSize = " + this.closeButtonStatus.closeButtonSize + ";
    var buttonAdjust = buttonSize;
    if (gn_delivery.style.transform.split("scale(")[1]){
      var s = parseFloat(gn_delivery.style.transform.split("scale(")[1].split(")")[0]);
      buttonSize *= s;
      buttonAdjust = buttonSize + parseFloat(document.getElementById("geniee_overlay").style.height) * (s-1);
    }
    closeIcon.src = "" + this.closeButtonStatus.closeButtonUrl + "";
    closeIcon.style.width = buttonSize + "px";
    closeIcon.style.height = buttonSize + "px";
    closeIcon.style.position = "absolute";
    closeIcon.style.top = "-" + buttonAdjust + "px";
    " + closeButtonPosition + "    closeIcon.setAttribute("id", "geniee_overlay_close");
    geniee_overlay.insertBefore(closeIcon, gn_delivery);
    closeIcon.addEventListener("click",function(){
        geniee_overlay.dataset["isRefresh"] = "false";
        geniee_overlay.style.display = "none";
    });
})(window, document);
</scr" + "ipt>
";
        this.append += closeButtonSetting;
      }
    },

    inputPreApp: function () {
      this.inputPrepend();
      this.inputAppend();
      this.inputPageGAMTag();
    },

    inputPageGAMTag: function () {
      if (this.aladdinTag === "") return;
      var aladdinTagSrc = this.aladdinTag.split('src="')[1].split('">')[0];
      var selectCSS = this.zoneType === "スタンダードバナー" ? "forAsyncSB.css" : "forAsyncOV.css";
      this.creativeTag = "<div class="gnROV" data-ad-src="" + aladdinTagSrc + "" data-zoneid="" + this.zoneId + "" data-rotate-interval="" + this.rotateStatus.period + "" data-rotate-count="" + this.rotateStatus.count + "" data-is-wheel-in="" + this.rotateStatus.isWheelIn + "" data-remove-bottom-space="" + this.rotateStatus.isRemoveBottomSpace + "" data-remove-bottom-space-plus-translate="" + this.rotateStatus.isUpshift + "" data-oicon-border="" + this.rotateStatus.iconSize + "" data-is-refresh="true" style="height: " + this.height + "px;">
  <iframe class="gnROVin"></iframe>
</div>
<link rel="stylesheet" type="text/css" href="https://works.gsspcln.jp/w/ad_format/" + selectCSS + ""></link>
<script type="text/javascript" src="https://works.gsspcln.jp/w/ad_format/gnRotateBannerPrd_v2.js"></scr" + "ipt>
<script>
  window.top.addEventListener("message", function (e) {
    if(e.data.action === "linkNotification") window.top.open(e.data.message, "_top");
  }, false);
</scr" + "ipt>";
      if (this.isExtendIn === "Aladdin配信時のみ" && this.deliveryViaGAM === "GAM経由配信"){
        var aladdinExtendCode = "<script>
    (function (window, document) {
        window.addEventListener('load', function () {
            if (typeof window.__gn_gam_expand_params === 'undefined') return;
            var s = parent.window.innerWidth / 320,
                BOOTS_HEIGHT = 0;
            var a = parent.document.getElementById('geniee_overlay_outer');

             a.style.webkitTransform = 'scale(' + s + ')';
            a.style.webkitTransformOrigin = 'bottom';
            a.style.transform = 'scale(' + s + ')';
            a.style.transformOrigin = 'bottom';

             if (parent.document.getElementById('gn_expand_area') !== null) {
                a.style.bottom = BOOTS_HEIGHT * s + 'px';
            }

             var b = document.getElementsByClassName('gnROVin')[0].contentWindow.document.getElementById('geniee_overlay');

             b.style.webkitTransform = 'scale(1)';
            b.style.transform = 'scale(1)';
        }, false);
    })(window, document);
</scr" + "ipt>";
        this.creativeTag += aladdinExtendCode;
        this.append += "<script>
    parent.window.__gn_gam_expand_params = true;
</scr" + "ipt>
";
      }
      if (this.rotateStatus.isUpshift && this.deliveryViaGAM === "GAM経由配信"){
        var aladdinUpshiftCode = "<script>
    (function (window, document) {
        window.addEventListener('load', function () {
            if (typeof window.__gn_gam_upshift_params === 'undefined') return;
            var a = document.getElementsByClassName('gnROVin')[0].contentWindow.document.getElementById('geniee_overlay'),
                d = parent.document,
                w = parent.window,
                h = 0,
                ov_base = d.getElementById('geniee_overlay_outer'),
                banner_height = parseFloat(a.style.height),
                boots_height = 0,
                s = parent.window.innerWidth / 320;
            if (d.getElementById('gn_outer_expand_area')) boots_height = parseFloat(d.getElementById('gn_outer_expand_area').style.height);

             w.addEventListener('scroll', function () {
                h = Math.max.apply(null, [d.body.clientHeight, d.body.scrollHeight, d.documentElement.scrollHeight, d.documentElement.clientHeight]);
                var sc_top = d.body.scrollTop || d.documentElement.scrollTop,
                    scr_h = w.innerHeight;
                if (h <= sc_top + scr_h + banner_height + boots_height) {
                    ov_base.style.bottom = '';
                    ov_base.style.top = '0px';
                    ov_base.style.webkitTransformOrigin = 'top';
                    ov_base.style.transformOrigin = 'top';
                } else {
                    ov_base.style.bottom = '0px';
                    ov_base.style.top = '';
                    ov_base.style.webkitTransformOrigin = 'bottom';
                    ov_base.style.transformOrigin = 'bottom';
                }
            }, false);
        }, false);
    })(window, document);
</scr" + "ipt>";
        this.creativeTag += aladdinUpshiftCode;
        this.append += "
<script>
    parent.window.__gn_gam_upshift_params = true;
</scr" + "ipt>
";
      }
      if (this.closeButtonAdd ===  "Aladdin配信時のみ" && this.deliveryViaGAM === "GAM経由配信"){
        var aladdinCloseButtonPosition = "";
        aladdinCloseButtonPosition = "closeIcon.style.left = "0px";
";
        if (this.closeButtonStatus.closeButtonPos === "左") aladdinCloseButtonPosition = "closeIcon.style.left = "0px";
";
        else if (this.closeButtonStatus.closeButtonPos === "中央") aladdinCloseButtonPosition = "";
        else if (this.closeButtonStatus.closeButtonPos === "右") aladdinCloseButtonPosition = "closeIcon.style.right = "0px";
";
        var aladdinCloseButtonCode = "<script>
    var geniee_overlay = parent.document.getElementById('geniee_overlay_outer');
    var closeIcon = document.createElement('img');
    var buttonSize = " + this.closeButtonStatus.closeButtonSize + ";
    closeIcon.src = '" + this.closeButtonStatus.closeButtonUrl + "';
    closeIcon.style.width = buttonSize + 'px';
    closeIcon.style.height = buttonSize + 'px';
    closeIcon.style.position = 'absolute';
    closeIcon.style.top = '-' + buttonSize + 'px';
    " + aladdinCloseButtonPosition + "
    var gn_delivery = geniee_overlay.getElementsByTagName('div')[1];
    geniee_overlay.getElementsByTagName('div')[0].insertBefore(closeIcon, gn_delivery);
    closeIcon.addEventListener('click',function(){
        document.getElementsByClassName('gnROV')[0].dataset['isRefresh'] = 'false';
        geniee_overlay.style.display = 'none';
    });
</scr" + "ipt>
";
        this.creativeTag += aladdinCloseButtonCode;
      }
    },


    processingGAMTag: function () {
      var headTag = this.normalGAMheadTag;
      var bodyTag = this.normalGAMbodyTag;
      this.GAMheadTag = headTagProcess(headTag, this.zoneId, this.isExtendIn, this.closeButtonAdd, this.closeButtonStatus, this.prBarStatus);
      this.GAMbodyTag = bodyTagProcess(bodyTag, this.zoneId);

      function replaceSearch (target, start, end){
          let s = target.indexOf(start),
              el = end.length,
              e = target.indexOf(end, s + 1) + el,
              result = target.slice(s, e);
          return result;
      }
      function headTagProcess (normal, zoneId, isExtendIn, closeButtonAdd, closeButtonStatus, prBarStatus) {
        var divId = replaceSearch(normal, "'div-gpt-ad-", "')").slice(1).slice(0,-2);
        var tag = normal.replace(divId, zoneId);

        var forGAMChange = false;
          var tagPlusCode = "    
googletag.pubads().addEventListener('slotRenderEnded', function(event) {
            if (event.slot !== targetSlot) return;
            var geniee_overlay_outer = parent.document.getElementById('geniee_overlay_outer');
            if (geniee_overlay_outer.getElementsByTagName('iframe') === null) return;
            if (geniee_overlay_outer.getElementsByTagName('iframe').length) {
                var geniee_overlay_outer = document.getElementById('geniee_overlay_outer');
                ";

          if (isExtendIn === "Aladdin配信時・AdX配信時ともに") {
              forGAMChange = true;
              var tagExtendCode = "var s = parent.window.innerWidth / geniee_overlay_outer.getElementsByTagName('iframe')[0].clientWidth,
                    BOOTS_HEIGHT = " + prBarStatus.prTextHeight + ";
                geniee_overlay_outer.style.webkitTransform = 'scale(' + s + ')';
                geniee_overlay_outer.style.webkitTransformOrigin = 'bottom';
                geniee_overlay_outer.style.transform = 'scale(' + s + ')';
                geniee_overlay_outer.style.transformOrigin = 'bottom';
                if (parent.document.getElementById('gn_expand_area') !== null) {
                    geniee_overlay_outer.style.bottom = BOOTS_HEIGHT * s + 'px';
                }
                ";
              tagPlusCode += tagExtendCode;
          }
          if (closeButtonAdd === "Aladdin配信時・AdX配信時ともに") {
              forGAMChange = true;
              var tagCloseButtonPosition = "";
              tagCloseButtonPosition = "closeIcon.style.left = "0px";
";
              if (closeButtonStatus.closeButtonPos === "左") tagCloseButtonPosition = "closeIcon.style.left = "0px";
";
              else if (closeButtonStatus.closeButtonPos === "中央") tagCloseButtonPosition = "";
              else if (closeButtonStatus.closeButtonPos === "右") tagCloseButtonPosition = "closeIcon.style.right = "0px";
";
              var tagCloseButtonCode = "var closeIcon = document.createElement('img');
                var BUTTON_WIDTH = " + closeButtonStatus.closeButtonSize + ";
                closeIcon.src = '" + closeButtonStatus.closeButtonUrl + "';
                closeIcon.style.width = BUTTON_WIDTH + 'px';
                closeIcon.style.verticalAlign = 'bottom';
                    " + tagCloseButtonPosition + "    closeIcon.addEventListener('click', function () {
                    geniee_overlay_outer.style.display = 'none';
                });
                geniee_overlay_outer.getElementsByTagName('div')[0].insertBefore(closeIcon, geniee_overlay_outer.getElementsByTagName('div')[1]);
                ";
              tagPlusCode += tagCloseButtonCode;
          }

          if (isExtendIn !== "Aladdin配信時・AdX配信時ともに" && closeButtonAdd !== "Aladdin配信時・AdX配信時ともに") forGAMChange = false;

          if (forGAMChange) {
              var headTagInsertPosition = tag.indexOf("googletag.enableServices();") + 27;
              var firstHead = tag.slice(0, headTagInsertPosition);
              var secondHead = tag.slice(headTagInsertPosition);
              tag = firstHead + tagPlusCode + "            }
            geniee_overlay_outer.style.opacity = '1';
        });
" + secondHead;
          }

        return tag;
      }

      function bodyTagProcess (normal, zoneId) {
        var divId = new RegExp(replaceSearch(normal, "'div-gpt-ad-", "'").slice(1).slice(0,-1), "g");

        normal = normal.replace(divId, zoneId);
        var tag = "<div id="geniee_overlay_outer" style="position:fixed; bottom: 0px;left:0px; right:0px; margin:auto; z-index:1000000000;width:100%;">
  " + normal + "
</div>";
        //if (isExtendIn === "Aladdin配信時・AdX配信時ともに") tag += "<script>
    (function (window, document) {
        window.addEventListener("load", function () {
            setTimeout(function () {
                var geniee_overlay_outer = parent.document.getElementById("geniee_overlay_outer");

                 if (geniee_overlay_outer.getElementsByTagName("iframe").length) {
                    var s = parent.window.innerWidth / geniee_overlay_outer.getElementsByTagName("iframe")[0].clientWidth,
                        BOOTS_HEIGHT = 0;
                    geniee_overlay_outer.style.webkitTransform = "scale(" + s + ")";
                    geniee_overlay_outer.style.webkitTransformOrigin = "bottom";
                    geniee_overlay_outer.style.transform = "scale(" + s + ")";
                    geniee_overlay_outer.style.transformOrigin = "bottom";

                    if (parent.document.getElementById("gn_expand_area") !== null) {
                        geniee_overlay_outer.style.bottom = BOOTS_HEIGHT * s + "px";
                    }
                }
            }, 500);
        }, false);
    })(window, document);
</scr" + "ipt>
"

        /*if (closeButtonAdd === "Aladdin配信時・AdX配信時ともに"){
          var closeButtonElement = " 
<div id="geniee_overlay_inner" style="position: relative;margin: auto; width: 320px;">
        <img src="" + closeButtonStatus.closeButtonUrl +"" width="" + closeButtonStatus.closeButtonSize +"" height="" + closeButtonStatus.closeButtonSize +"" id="geniee_overlay_close" style="position: absolute; top: -" + closeButtonStatus.closeButtonSize +"px;"; // left: 0;">
 ";
          if (closeButtonStatus.closeButtonPos === "左") closeButtonElement += "left: 0;">
 ";
          else if (closeButtonStatus.closeButtonPos === "右") closeButtonElement += "right: 0;">
 ";
          else if (closeButtonStatus.closeButtonPos === "中央") closeButtonElement += "left: 0; right: 0; margin: auto;">
 ";

          var divide1 = "<div id="" + zoneId +""";
          var divide1pos = tag.indexOf(divide1);
          if (divide1pos < 0){
            divide1 = "<div id='" + zoneId +"'";
            divide1pos = tag.indexOf(divide1);
          }
          tag = tag.slice(0,divide1pos) + closeButtonElement + tag.slice(divide1pos);

          var divide2 = "</div>";
          var divide2pos = tag.lastIndexOf(divide2);
          tag = tag.slice(0,divide2pos) + " </div>
    " + tag.slice(divide2pos);

          tag += "<script>
    (function (window, document) {
        var geniee_overlay_outer = document.getElementById("geniee_overlay_outer");
        var geniee_overlay_inner = document.getElementById("geniee_overlay_inner");
        var closeIcon = document.getElementById("geniee_overlay_close");

         closeIcon.addEventListener("click", function () {
            geniee_overlay_outer.getElementsByTagName("iframe")[0].contentWindow.document.getElementsByClassName("gnROV")[0].dataset["isRefresh"] = "false";
           geniee_overlay_outer.style.display = "none";
        });
        window.addEventListener("load", function () {
            setTimeout(function () {
                if (geniee_overlay_outer.getElementsByTagName("iframe").length) {
                    var iframe = geniee_overlay_outer.getElementsByTagName("iframe")[0];
                    var iframeWidth = iframe.clientWidth;
                    geniee_overlay_inner.style.width = iframeWidth + "px";
                    document.getElementById("" + zoneId + "").style.margin = "0";
                }
                geniee_overlay_outer.style.opacity = "1";
            }, 2000);
        }, false);
    })(window, document);
</scr" + "ipt>
";
        }*/
        return tag;
      }
    },


    inputAll: function () {
      this.inputPrepend();
      this.inputAppend();
      this.inputPageGAMTag();
      this.processingGAMTag();
    },
  }
}
